#include <stdio.h>

int main() {
    float temperature, converted;
    char unit;

    // Read temperature and unit
    scanf("%f %c", &temperature, &unit);

    // Convert based on the unit
    if (unit == 'F' || unit == 'f') {
        converted = (temperature - 32) * 5 / 9;
        printf("%.1f Celsius\n", converted);
    } else if (unit == 'C' || unit == 'c') {
        converted = (temperature * 9 / 5) + 32;
        printf("%.1f Fahrenheit\n", converted);
    } else {
        printf("Invalid unit. Please use F for Fahrenheit or C for Celsius.\n");
    }

    return 0;
}
